let scrollInterval;function scrollSlider(e,l){const t=document.getElementById(e),r="left"===l?-10:10;scrollInterval=setInterval(()=>{t.scrollLeft+=r},10)}function stopScrolling(){clearInterval(scrollInterval)}document.querySelectorAll(".arrow").forEach(e=>{const l=e.getAttribute("data-slider"),t=e.getAttribute("data-action");e.addEventListener("mousedown",()=>{"scrollLeft"===t?scrollSlider(l,"left"):"scrollRight"===t&&scrollSlider(l,"right")}),e.addEventListener("mouseup",stopScrolling),e.addEventListener("mouseleave",stopScrolling)});